var mongoose = require('mongoose');

var Schema = mongoose.Schema;
var itemSchema = new Schema({
  name: {
    type: String
  },
  description: {
    type: String
  },
  price: {
    type: Number
  },
  category: {
    type: String
  },
  subCategory: {
    type: String
  },
  image: {
    type: String
  },
  popularity: {
    // +1 za kazdym razem gdy ktoz kliknie i na bazie tego bd opcja popularity
    type: Number,
    "default": 0
  }
}, {
  timestamps: true
});
var Item = mongoose.models.itemModel || mongoose.model('item', itemSchema);
module.exports = Item;
